INSERT INTO `goods` (`goodid`, `goodname`, `goodprice`, `goodcontext`, `goodcover`, `goodtype`, `goodnum`) VALUES (62, '土豆', 1.2, '马铃薯是茄科茄属的一年生草本植物。地上茎呈菱形，有毛。转基因土豆：表面光滑。马铃薯叶片初生时为单叶，逐渐生长成奇数不相等羽状复叶，大小相间，呈卵形至长圆形；伞房花序生长在顶部，花为白色或蓝紫色；果实为浆果；块茎扁圆形或球形，无毛或被疏柔毛；薯皮白色、淡红色或紫色；薯肉有白、淡黄、黄色等色。花期夏季。马铃薯因酷似马铃铛而得名。', 'UpLoadFiles/62/121783f4-fc7c-4989-a2b8-cf2446ffdb7a_OIP-C.jpg', '蔬菜', 100);
INSERT INTO `goods` (`goodid`, `goodname`, `goodprice`, `goodcontext`, `goodcover`, `goodtype`, `goodnum`) VALUES (63, '大土豆', 1.8, '大土豆是已经成熟了的土豆，我们常用来炖汤、煲汤、红烧、炸土豆条，切土豆片和磨土豆粉。因为这些烹饪方式需要的是成熟的土豆，淀粉含量高，味道偏粉。', 'UpLoadFiles/63/17e55337-b3e8-4af2-aab2-5e9c9f63239e_th.jpg', '蔬菜', 290);
INSERT INTO `goods` (`goodid`, `goodname`, `goodprice`, `goodcontext`, `goodcover`, `goodtype`, `goodnum`) VALUES (64, '香蕉', 1.2, '香蕉，芭蕉科芭蕉属多年生草本植物，植株丛生，有匐匍茎；假茎浓绿有黑色斑点；叶片长圆形，上面为深绿色，无白粉，下面浅绿色；花朵为乳白色或淡紫色；果实呈弯曲的弓状，有棱，果皮为青绿色，成熟后变黄；果肉松软，黄白色，味甜香味浓，无种子。', 'UpLoadFiles/64/56d8dfcd-2c3d-4bb8-9cb9-7fd9e16b47f3_OIP-C1.jpg', '水果', 194);
